/*
 * chat_completion_stream_response_delta.h
 *
 * A chat completion delta generated by streamed model responses.
 */

#ifndef _chat_completion_stream_response_delta_H_
#define _chat_completion_stream_response_delta_H_

#include <string.h>
#include "../external/cJSON.h"
#include "../include/list.h"
#include "../include/keyValuePair.h"
#include "../include/binary.h"

typedef struct chat_completion_stream_response_delta_t chat_completion_stream_response_delta_t;

#include "chat_completion_message_tool_call_chunk.h"
#include "chat_completion_stream_response_delta_function_call.h"

// Enum ROLE for chat_completion_stream_response_delta

typedef enum  { openai_api_chat_completion_stream_response_delta_ROLE_NULL = 0, openai_api_chat_completion_stream_response_delta_ROLE_system, openai_api_chat_completion_stream_response_delta_ROLE_user, openai_api_chat_completion_stream_response_delta_ROLE_assistant, openai_api_chat_completion_stream_response_delta_ROLE_tool } openai_api_chat_completion_stream_response_delta_ROLE_e;

char* chat_completion_stream_response_delta_role_ToString(openai_api_chat_completion_stream_response_delta_ROLE_e role);

openai_api_chat_completion_stream_response_delta_ROLE_e chat_completion_stream_response_delta_role_FromString(char* role);



typedef struct chat_completion_stream_response_delta_t {
    char *content; // string
    struct chat_completion_stream_response_delta_function_call_t *function_call; //model
    list_t *tool_calls; //nonprimitive container
    openai_api_chat_completion_stream_response_delta_ROLE_e role; //enum

} chat_completion_stream_response_delta_t;

chat_completion_stream_response_delta_t *chat_completion_stream_response_delta_create(
    char *content,
    chat_completion_stream_response_delta_function_call_t *function_call,
    list_t *tool_calls,
    openai_api_chat_completion_stream_response_delta_ROLE_e role
);

void chat_completion_stream_response_delta_free(chat_completion_stream_response_delta_t *chat_completion_stream_response_delta);

chat_completion_stream_response_delta_t *chat_completion_stream_response_delta_parseFromJSON(cJSON *chat_completion_stream_response_deltaJSON);

cJSON *chat_completion_stream_response_delta_convertToJSON(chat_completion_stream_response_delta_t *chat_completion_stream_response_delta);

#endif /* _chat_completion_stream_response_delta_H_ */

