void OAIFineTuningApi::retrieveFineTuningJob(const QString &fine_tuning_job_id) {
    QString fullPath = QString(_serverConfigs["retrieveFineTuningJob"][_serverIndices.value("retrieveFineTuningJob")].URL()+"/fine_tuning/jobs/{fine_tuning_job_id}");
    
    if (!_bearerToken.isEmpty())
        addHeaders("Authorization", "Bearer " + _bearerToken);
    
    
    {
        QString fine_tuning_job_idPathParam("{");
        fine_tuning_job_idPathParam.append("fine_tuning_job_id").append("}");
        QString pathPrefix, pathSuffix, pathDelimiter;
        QString pathStyle = "simple";
        if (pathStyle == "")
            pathStyle = "simple";
        pathPrefix = getParamStylePrefix(pathStyle);
        pathSuffix = getParamStyleSuffix(pathStyle);
        pathDelimiter = getParamStyleDelimiter(pathStyle, "fine_tuning_job_id", false);
        QString paramString = (pathStyle == "matrix") ? pathPrefix+"fine_tuning_job_id"+pathSuffix : pathPrefix;
        fullPath.replace(fine_tuning_job_idPathParam, paramString+QUrl::toPercentEncoding(::OpenAPI::toStringValue(fine_tuning_job_id)));
    }
    OAIHttpRequestWorker *worker = new OAIHttpRequestWorker(this, _manager);
    worker->setTimeOut(_timeOut);
    worker->setWorkingDirectory(_workingDirectory);
    OAIHttpRequestInput input(fullPath, "GET");


    for (auto keyValueIt = _defaultHeaders.keyValueBegin(); keyValueIt != _defaultHeaders.keyValueEnd(); keyValueIt++) {
        input.headers.insert(keyValueIt->first, keyValueIt->second);
    }


    connect(worker, &OAIHttpRequestWorker::on_execution_finished, this, &OAIFineTuningApi::retrieveFineTuningJobCallback);
    connect(this, &OAIFineTuningApi::abortRequestsSignal, worker, &QObject::deleteLater);
    connect(worker, &QObject::destroyed, this, [this]() {
        if (findChildren<OAIHttpRequestWorker*>().count() == 0) {
            Q_EMIT allPendingRequestsCompleted();
        }
    });

    worker->execute(&input);
}

