        public const string BASE_ADDRESS = "https://api.openai.com/v1";

        /// <summary>
        /// The scheme of the API
        /// </summary>
        public const string SCHEME = "https";

        /// <summary>
        /// The context path of the API
        /// </summary>
        public const string CONTEXT_PATH = "/v1";

        /// <summary>
        /// The host of the API
        /// </summary>
        public const string HOST = "api.openai.com";

        /// <summary>
