<?php
declare(strict_types=1);

namespace App\DTO;

use Articus\DataTransfer\Annotation as DTA;

/**
 * Breakdown of tokens used in a completion.
 */
class CompletionUsageCompletionTokensDetails
{
    /**
     * When using Predicted Outputs, the number of tokens in the prediction that appeared in the completion.
     * @DTA\Data(field="accepted_prediction_tokens", nullable=true)
     * @DTA\Validator(name="Scalar", options={"type":"int"})
     */
    public ?int $accepted_prediction_tokens = null;

    /**
     * Audio input tokens generated by the model.
     * @DTA\Data(field="audio_tokens", nullable=true)
     * @DTA\Validator(name="Scalar", options={"type":"int"})
     */
    public ?int $audio_tokens = null;

    /**
     * Tokens generated by the model for reasoning.
     * @DTA\Data(field="reasoning_tokens", nullable=true)
     * @DTA\Validator(name="Scalar", options={"type":"int"})
     */
    public ?int $reasoning_tokens = null;

    /**
     * When using Predicted Outputs, the number of tokens in the prediction that did not appear in the completion. However, like reasoning tokens, these tokens are still counted in the total completion tokens for purposes of billing, output, and context window limits.
     * @DTA\Data(field="rejected_prediction_tokens", nullable=true)
     * @DTA\Validator(name="Scalar", options={"type":"int"})
     */
    public ?int $rejected_prediction_tokens = null;

}
