<?php
declare(strict_types=1);

namespace App\DTO;

use Articus\DataTransfer\Annotation as DTA;

/**
 * A chat completion message generated by the model.
 */
class ChatCompletionResponseMessage
{
    /**
     * The contents of the message.
     * @DTA\Data(field="content")
     * @DTA\Validator(name="Scalar", options={"type":"string"})
     * @var string|null
     */
    public $content;

    /**
     * The refusal message generated by the model.
     * @DTA\Data(field="refusal")
     * @DTA\Validator(name="Scalar", options={"type":"string"})
     * @var string|null
     */
    public $refusal;

    /**
     * The tool calls generated by the model, such as function calls.
     * @DTA\Data(field="tool_calls", nullable=true)
     * @DTA\Strategy(name="Object", options={"type":\App\DTO\Collection23::class})
     * @DTA\Validator(name="TypeCompliant", options={"type":\App\DTO\Collection23::class})
     * @var \App\DTO\Collection23|null
     */
    public $tool_calls;

    /**
     * The role of the author of this message.
     * @DTA\Data(field="role")
     * @DTA\Validator(name="Scalar", options={"type":"string"})
     * @var string|null
     */
    public $role;

    /**
     * @DTA\Data(field="function_call", nullable=true)
     * @DTA\Strategy(name="Object", options={"type":\App\DTO\ChatCompletionResponseMessageFunctionCall::class})
     * @DTA\Validator(name="TypeCompliant", options={"type":\App\DTO\ChatCompletionResponseMessageFunctionCall::class})
     * @var \App\DTO\ChatCompletionResponseMessageFunctionCall|null
     */
    public $function_call;

    /**
     * @DTA\Data(field="audio", nullable=true)
     * @DTA\Strategy(name="Object", options={"type":\App\DTO\ChatCompletionResponseMessageAudio::class})
     * @DTA\Validator(name="TypeCompliant", options={"type":\App\DTO\ChatCompletionResponseMessageAudio::class})
     * @var \App\DTO\ChatCompletionResponseMessageAudio|null
     */
    public $audio;

}
