#' Create a new ChatCompletionResponseMessage
#'
#' @description
#' A chat completion message generated by the model.
#'
#' @docType class
#' @title ChatCompletionResponseMessage
#' @description ChatCompletionResponseMessage Class
#' @format An \code{R6Class} generator object
#' @field content The contents of the message. character
#' @field tool_calls The tool calls generated by the model, such as function calls. list(\link{ChatCompletionMessageToolCall}) [optional]
#' @field role The role of the author of this message. character
#' @field function_call  \link{ChatCompletionRequestAssistantMessageFunctionCall} [optional]
#' @importFrom R6 R6Class
#' @importFrom jsonlite fromJSON toJSON
#' @export
ChatCompletionResponseMessage <- R6::R6Class(
  "ChatCompletionResponseMessage",
  public = list(
    `content` = NULL,
    `tool_calls` = NULL,
    `role` = NULL,
    `function_call` = NULL,
    #' Initialize a new ChatCompletionResponseMessage class.
    #'
    #' @description
    #' Initialize a new ChatCompletionResponseMessage class.
    #'
    #' @param content The contents of the message.
    #' @param role The role of the author of this message.
    #' @param tool_calls The tool calls generated by the model, such as function calls.
    #' @param function_call function_call
    #' @param ... Other optional arguments.
    #' @export
    initialize = function(`content`, `role`, `tool_calls` = NULL, `function_call` = NULL, ...) {
      if (!missing(`content`)) {
        if (!(is.character(`content`) && length(`content`) == 1)) {
          stop(paste("Error! Invalid data for `content`. Must be a string:", `content`))
        }
        self$`content` <- `content`
      }
      if (!missing(`role`)) {
        if (!(`role` %in% c("assistant"))) {
          stop(paste("Error! \"", `role`, "\" cannot be assigned to `role`. Must be \"assistant\".", sep = ""))
        }
        if (!(is.character(`role`) && length(`role`) == 1)) {
          stop(paste("Error! Invalid data for `role`. Must be a string:", `role`))
        }
        self$`role` <- `role`
      }
      if (!is.null(`tool_calls`)) {
        stopifnot(is.vector(`tool_calls`), length(`tool_calls`) != 0)
        sapply(`tool_calls`, function(x) stopifnot(R6::is.R6(x)))
        self$`tool_calls` <- `tool_calls`
      }
      if (!is.null(`function_call`)) {
        stopifnot(R6::is.R6(`function_call`))
        self$`function_call` <- `function_call`
      }
    },
    #' To JSON string
    #'
    #' @description
    #' To JSON String
    #'
    #' @return ChatCompletionResponseMessage in JSON format
    #' @export
    toJSON = function() {
      ChatCompletionResponseMessageObject <- list()
      if (!is.null(self$`content`)) {
        ChatCompletionResponseMessageObject[["content"]] <-
          self$`content`
      }
      if (!is.null(self$`tool_calls`)) {
        ChatCompletionResponseMessageObject[["tool_calls"]] <-
          lapply(self$`tool_calls`, function(x) x$toJSON())
      }
      if (!is.null(self$`role`)) {
        ChatCompletionResponseMessageObject[["role"]] <-
          self$`role`
      }
      if (!is.null(self$`function_call`)) {
        ChatCompletionResponseMessageObject[["function_call"]] <-
          self$`function_call`$toJSON()
      }
      ChatCompletionResponseMessageObject
    },
    #' Deserialize JSON string into an instance of ChatCompletionResponseMessage
    #'
    #' @description
    #' Deserialize JSON string into an instance of ChatCompletionResponseMessage
    #'
    #' @param input_json the JSON input
    #' @return the instance of ChatCompletionResponseMessage
    #' @export
    fromJSON = function(input_json) {
      this_object <- jsonlite::fromJSON(input_json)
      if (!is.null(this_object$`content`)) {
        self$`content` <- this_object$`content`
      }
      if (!is.null(this_object$`tool_calls`)) {
        self$`tool_calls` <- ApiClient$new()$deserializeObj(this_object$`tool_calls`, "array[ChatCompletionMessageToolCall]", loadNamespace("openapi"))
      }
      if (!is.null(this_object$`role`)) {
        if (!is.null(this_object$`role`) && !(this_object$`role` %in% c("assistant"))) {
          stop(paste("Error! \"", this_object$`role`, "\" cannot be assigned to `role`. Must be \"assistant\".", sep = ""))
        }
        self$`role` <- this_object$`role`
      }
      if (!is.null(this_object$`function_call`)) {
        `function_call_object` <- ChatCompletionRequestAssistantMessageFunctionCall$new()
        `function_call_object`$fromJSON(jsonlite::toJSON(this_object$`function_call`, auto_unbox = TRUE, digits = NA))
        self$`function_call` <- `function_call_object`
      }
      self
    },
    #' To JSON string
    #'
    #' @description
    #' To JSON String
    #'
    #' @return ChatCompletionResponseMessage in JSON format
    #' @export
    toJSONString = function() {
      jsoncontent <- c(
        if (!is.null(self$`content`)) {
          sprintf(
          '"content":
            "%s"
                    ',
          self$`content`
          )
        },
        if (!is.null(self$`tool_calls`)) {
          sprintf(
          '"tool_calls":
          [%s]
',
          paste(sapply(self$`tool_calls`, function(x) jsonlite::toJSON(x$toJSON(), auto_unbox = TRUE, digits = NA)), collapse = ",")
          )
        },
        if (!is.null(self$`role`)) {
          sprintf(
          '"role":
            "%s"
                    ',
          self$`role`
          )
        },
        if (!is.null(self$`function_call`)) {
          sprintf(
          '"function_call":
          %s
          ',
          jsonlite::toJSON(self$`function_call`$toJSON(), auto_unbox = TRUE, digits = NA)
          )
        }
      )
      jsoncontent <- paste(jsoncontent, collapse = ",")
      json_string <- as.character(jsonlite::minify(paste("{", jsoncontent, "}", sep = "")))
    },
    #' Deserialize JSON string into an instance of ChatCompletionResponseMessage
    #'
    #' @description
    #' Deserialize JSON string into an instance of ChatCompletionResponseMessage
    #'
    #' @param input_json the JSON input
    #' @return the instance of ChatCompletionResponseMessage
    #' @export
    fromJSONString = function(input_json) {
      this_object <- jsonlite::fromJSON(input_json)
      self$`content` <- this_object$`content`
      self$`tool_calls` <- ApiClient$new()$deserializeObj(this_object$`tool_calls`, "array[ChatCompletionMessageToolCall]", loadNamespace("openapi"))
      if (!is.null(this_object$`role`) && !(this_object$`role` %in% c("assistant"))) {
        stop(paste("Error! \"", this_object$`role`, "\" cannot be assigned to `role`. Must be \"assistant\".", sep = ""))
      }
      self$`role` <- this_object$`role`
      self$`function_call` <- ChatCompletionRequestAssistantMessageFunctionCall$new()$fromJSON(jsonlite::toJSON(this_object$`function_call`, auto_unbox = TRUE, digits = NA))
      self
    },
    #' Validate JSON input with respect to ChatCompletionResponseMessage
    #'
    #' @description
    #' Validate JSON input with respect to ChatCompletionResponseMessage and throw an exception if invalid
    #'
    #' @param input the JSON input
    #' @export
    validateJSON = function(input) {
      input_json <- jsonlite::fromJSON(input)
      # check the required field `content`
      if (!is.null(input_json$`content`)) {
        if (!(is.character(input_json$`content`) && length(input_json$`content`) == 1)) {
          stop(paste("Error! Invalid data for `content`. Must be a string:", input_json$`content`))
        }
      } else {
        stop(paste("The JSON input `", input, "` is invalid for ChatCompletionResponseMessage: the required field `content` is missing."))
      }
      # check the required field `role`
      if (!is.null(input_json$`role`)) {
        if (!(is.character(input_json$`role`) && length(input_json$`role`) == 1)) {
          stop(paste("Error! Invalid data for `role`. Must be a string:", input_json$`role`))
        }
      } else {
        stop(paste("The JSON input `", input, "` is invalid for ChatCompletionResponseMessage: the required field `role` is missing."))
      }
    },
    #' To string (JSON format)
    #'
    #' @description
    #' To string (JSON format)
    #'
    #' @return String representation of ChatCompletionResponseMessage
    #' @export
    toString = function() {
      self$toJSONString()
    },
    #' Return true if the values in all fields are valid.
    #'
    #' @description
    #' Return true if the values in all fields are valid.
    #'
    #' @return true if the values in all fields are valid.
    #' @export
    isValid = function() {
      # check if the required `role` is null
      if (is.null(self$`role`)) {
        return(FALSE)
      }

      TRUE
    },
    #' Return a list of invalid fields (if any).
    #'
    #' @description
    #' Return a list of invalid fields (if any).
    #'
    #' @return A list of invalid fields (if any).
    #' @export
    getInvalidFields = function() {
      invalid_fields <- list()
      # check if the required `role` is null
      if (is.null(self$`role`)) {
        invalid_fields["role"] <- "Non-nullable required field `role` cannot be null."
      }

      invalid_fields
    },
    #' Print the object
    #'
    #' @description
    #' Print the object
    #'
    #' @export
    print = function() {
      print(jsonlite::prettify(self$toJSONString()))
      invisible(self)
    }
  ),
  # Lock the class to prevent modifications to the method or field
  lock_class = TRUE
)
