#' Create a new ChatCompletionStreamResponseDelta
#'
#' @description
#' A chat completion delta generated by streamed model responses.
#'
#' @docType class
#' @title ChatCompletionStreamResponseDelta
#' @description ChatCompletionStreamResponseDelta Class
#' @format An \code{R6Class} generator object
#' @field content The contents of the chunk message. character [optional]
#' @field function_call  \link{ChatCompletionStreamResponseDeltaFunctionCall} [optional]
#' @field tool_calls  list(\link{ChatCompletionMessageToolCallChunk}) [optional]
#' @field role The role of the author of this message. character [optional]
#' @field refusal The refusal message generated by the model. character [optional]
#' @importFrom R6 R6Class
#' @importFrom jsonlite fromJSON toJSON
#' @export
ChatCompletionStreamResponseDelta <- R6::R6Class(
  "ChatCompletionStreamResponseDelta",
  public = list(
    `content` = NULL,
    `function_call` = NULL,
    `tool_calls` = NULL,
    `role` = NULL,
    `refusal` = NULL,

    #' @description
    #' Initialize a new ChatCompletionStreamResponseDelta class.
    #'
    #' @param content The contents of the chunk message.
    #' @param function_call function_call
    #' @param tool_calls tool_calls
    #' @param role The role of the author of this message.
    #' @param refusal The refusal message generated by the model.
    #' @param ... Other optional arguments.
    initialize = function(`content` = NULL, `function_call` = NULL, `tool_calls` = NULL, `role` = NULL, `refusal` = NULL, ...) {
      if (!is.null(`content`)) {
        if (!(is.character(`content`) && length(`content`) == 1)) {
          stop(paste("Error! Invalid data for `content`. Must be a string:", `content`))
        }
        self$`content` <- `content`
      }
      if (!is.null(`function_call`)) {
        stopifnot(R6::is.R6(`function_call`))
        self$`function_call` <- `function_call`
      }
      if (!is.null(`tool_calls`)) {
        stopifnot(is.vector(`tool_calls`), length(`tool_calls`) != 0)
        sapply(`tool_calls`, function(x) stopifnot(R6::is.R6(x)))
        self$`tool_calls` <- `tool_calls`
      }
      if (!is.null(`role`)) {
        if (!(`role` %in% c("system", "user", "assistant", "tool"))) {
          stop(paste("Error! \"", `role`, "\" cannot be assigned to `role`. Must be \"system\", \"user\", \"assistant\", \"tool\".", sep = ""))
        }
        if (!(is.character(`role`) && length(`role`) == 1)) {
          stop(paste("Error! Invalid data for `role`. Must be a string:", `role`))
        }
        self$`role` <- `role`
      }
      if (!is.null(`refusal`)) {
        if (!(is.character(`refusal`) && length(`refusal`) == 1)) {
          stop(paste("Error! Invalid data for `refusal`. Must be a string:", `refusal`))
        }
        self$`refusal` <- `refusal`
      }
    },

    #' @description
    #' Convert to an R object. This method is deprecated. Use `toSimpleType()` instead.
    toJSON = function() {
      .Deprecated(new = "toSimpleType", msg = "Use the '$toSimpleType()' method instead since that is more clearly named. Use '$toJSONString()' to get a JSON string")
      return(self$toSimpleType())
    },

    #' @description
    #' Convert to a List
    #'
    #' Convert the R6 object to a list to work more easily with other tooling.
    #'
    #' @return ChatCompletionStreamResponseDelta as a base R list.
    #' @examples
    #' # convert array of ChatCompletionStreamResponseDelta (x) to a data frame
    #' \dontrun{
    #' library(purrr)
    #' library(tibble)
    #' df <- x |> map(\(y)y$toList()) |> map(as_tibble) |> list_rbind()
    #' df
    #' }
    toList = function() {
      return(self$toSimpleType())
    },

    #' @description
    #' Convert ChatCompletionStreamResponseDelta to a base R type
    #'
    #' @return A base R type, e.g. a list or numeric/character array.
    toSimpleType = function() {
      ChatCompletionStreamResponseDeltaObject <- list()
      if (!is.null(self$`content`)) {
        ChatCompletionStreamResponseDeltaObject[["content"]] <-
          self$`content`
      }
      if (!is.null(self$`function_call`)) {
        ChatCompletionStreamResponseDeltaObject[["function_call"]] <-
          self$`function_call`$toSimpleType()
      }
      if (!is.null(self$`tool_calls`)) {
        ChatCompletionStreamResponseDeltaObject[["tool_calls"]] <-
          lapply(self$`tool_calls`, function(x) x$toSimpleType())
      }
      if (!is.null(self$`role`)) {
        ChatCompletionStreamResponseDeltaObject[["role"]] <-
          self$`role`
      }
      if (!is.null(self$`refusal`)) {
        ChatCompletionStreamResponseDeltaObject[["refusal"]] <-
          self$`refusal`
      }
      return(ChatCompletionStreamResponseDeltaObject)
    },

    #' @description
    #' Deserialize JSON string into an instance of ChatCompletionStreamResponseDelta
    #'
    #' @param input_json the JSON input
    #' @return the instance of ChatCompletionStreamResponseDelta
    fromJSON = function(input_json) {
      this_object <- jsonlite::fromJSON(input_json)
      if (!is.null(this_object$`content`)) {
        self$`content` <- this_object$`content`
      }
      if (!is.null(this_object$`function_call`)) {
        `function_call_object` <- ChatCompletionStreamResponseDeltaFunctionCall$new()
        `function_call_object`$fromJSON(jsonlite::toJSON(this_object$`function_call`, auto_unbox = TRUE, digits = NA))
        self$`function_call` <- `function_call_object`
      }
      if (!is.null(this_object$`tool_calls`)) {
        self$`tool_calls` <- ApiClient$new()$deserializeObj(this_object$`tool_calls`, "array[ChatCompletionMessageToolCallChunk]", loadNamespace("openapi"))
      }
      if (!is.null(this_object$`role`)) {
        if (!is.null(this_object$`role`) && !(this_object$`role` %in% c("system", "user", "assistant", "tool"))) {
          stop(paste("Error! \"", this_object$`role`, "\" cannot be assigned to `role`. Must be \"system\", \"user\", \"assistant\", \"tool\".", sep = ""))
        }
        self$`role` <- this_object$`role`
      }
      if (!is.null(this_object$`refusal`)) {
        self$`refusal` <- this_object$`refusal`
      }
      self
    },

    #' @description
    #' To JSON String
    #' 
    #' @param ... Parameters passed to `jsonlite::toJSON`
    #' @return ChatCompletionStreamResponseDelta in JSON format
    toJSONString = function(...) {
      simple <- self$toSimpleType()
      json <- jsonlite::toJSON(simple, auto_unbox = TRUE, digits = NA, ...)
      return(as.character(jsonlite::minify(json)))
    },

    #' @description
    #' Deserialize JSON string into an instance of ChatCompletionStreamResponseDelta
    #'
    #' @param input_json the JSON input
    #' @return the instance of ChatCompletionStreamResponseDelta
    fromJSONString = function(input_json) {
      this_object <- jsonlite::fromJSON(input_json)
      self$`content` <- this_object$`content`
      self$`function_call` <- ChatCompletionStreamResponseDeltaFunctionCall$new()$fromJSON(jsonlite::toJSON(this_object$`function_call`, auto_unbox = TRUE, digits = NA))
      self$`tool_calls` <- ApiClient$new()$deserializeObj(this_object$`tool_calls`, "array[ChatCompletionMessageToolCallChunk]", loadNamespace("openapi"))
      if (!is.null(this_object$`role`) && !(this_object$`role` %in% c("system", "user", "assistant", "tool"))) {
        stop(paste("Error! \"", this_object$`role`, "\" cannot be assigned to `role`. Must be \"system\", \"user\", \"assistant\", \"tool\".", sep = ""))
      }
      self$`role` <- this_object$`role`
      self$`refusal` <- this_object$`refusal`
      self
    },

    #' @description
    #' Validate JSON input with respect to ChatCompletionStreamResponseDelta and throw an exception if invalid
    #'
    #' @param input the JSON input
    validateJSON = function(input) {
      input_json <- jsonlite::fromJSON(input)
    },

    #' @description
    #' To string (JSON format)
    #'
    #' @return String representation of ChatCompletionStreamResponseDelta
    toString = function() {
      self$toJSONString()
    },

    #' @description
    #' Return true if the values in all fields are valid.
    #'
    #' @return true if the values in all fields are valid.
    isValid = function() {
      TRUE
    },

    #' @description
    #' Return a list of invalid fields (if any).
    #'
    #' @return A list of invalid fields (if any).
    getInvalidFields = function() {
      invalid_fields <- list()
      invalid_fields
    },

    #' @description
    #' Print the object
    print = function() {
      print(jsonlite::prettify(self$toJSONString()))
      invisible(self)
    }
  ),
  # Lock the class to prevent modifications to the method or field
  lock_class = TRUE
)
