#' Create a new CompletionUsageCompletionTokensDetails
#'
#' @description
#' Breakdown of tokens used in a completion.
#'
#' @docType class
#' @title CompletionUsageCompletionTokensDetails
#' @description CompletionUsageCompletionTokensDetails Class
#' @format An \code{R6Class} generator object
#' @field accepted_prediction_tokens When using Predicted Outputs, the number of tokens in the prediction that appeared in the completion. integer [optional]
#' @field audio_tokens Audio input tokens generated by the model. integer [optional]
#' @field reasoning_tokens Tokens generated by the model for reasoning. integer [optional]
#' @field rejected_prediction_tokens When using Predicted Outputs, the number of tokens in the prediction that did not appear in the completion. However, like reasoning tokens, these tokens are still counted in the total completion tokens for purposes of billing, output, and context window limits. integer [optional]
#' @importFrom R6 R6Class
#' @importFrom jsonlite fromJSON toJSON
#' @export
CompletionUsageCompletionTokensDetails <- R6::R6Class(
  "CompletionUsageCompletionTokensDetails",
  public = list(
    `accepted_prediction_tokens` = NULL,
    `audio_tokens` = NULL,
    `reasoning_tokens` = NULL,
    `rejected_prediction_tokens` = NULL,

    #' @description
    #' Initialize a new CompletionUsageCompletionTokensDetails class.
    #'
    #' @param accepted_prediction_tokens When using Predicted Outputs, the number of tokens in the prediction that appeared in the completion.
    #' @param audio_tokens Audio input tokens generated by the model.
    #' @param reasoning_tokens Tokens generated by the model for reasoning.
    #' @param rejected_prediction_tokens When using Predicted Outputs, the number of tokens in the prediction that did not appear in the completion. However, like reasoning tokens, these tokens are still counted in the total completion tokens for purposes of billing, output, and context window limits.
    #' @param ... Other optional arguments.
    initialize = function(`accepted_prediction_tokens` = NULL, `audio_tokens` = NULL, `reasoning_tokens` = NULL, `rejected_prediction_tokens` = NULL, ...) {
      if (!is.null(`accepted_prediction_tokens`)) {
        if (!(is.numeric(`accepted_prediction_tokens`) && length(`accepted_prediction_tokens`) == 1)) {
          stop(paste("Error! Invalid data for `accepted_prediction_tokens`. Must be an integer:", `accepted_prediction_tokens`))
        }
        self$`accepted_prediction_tokens` <- `accepted_prediction_tokens`
      }
      if (!is.null(`audio_tokens`)) {
        if (!(is.numeric(`audio_tokens`) && length(`audio_tokens`) == 1)) {
          stop(paste("Error! Invalid data for `audio_tokens`. Must be an integer:", `audio_tokens`))
        }
        self$`audio_tokens` <- `audio_tokens`
      }
      if (!is.null(`reasoning_tokens`)) {
        if (!(is.numeric(`reasoning_tokens`) && length(`reasoning_tokens`) == 1)) {
          stop(paste("Error! Invalid data for `reasoning_tokens`. Must be an integer:", `reasoning_tokens`))
        }
        self$`reasoning_tokens` <- `reasoning_tokens`
      }
      if (!is.null(`rejected_prediction_tokens`)) {
        if (!(is.numeric(`rejected_prediction_tokens`) && length(`rejected_prediction_tokens`) == 1)) {
          stop(paste("Error! Invalid data for `rejected_prediction_tokens`. Must be an integer:", `rejected_prediction_tokens`))
        }
        self$`rejected_prediction_tokens` <- `rejected_prediction_tokens`
      }
    },

    #' @description
    #' Convert to an R object. This method is deprecated. Use `toSimpleType()` instead.
    toJSON = function() {
      .Deprecated(new = "toSimpleType", msg = "Use the '$toSimpleType()' method instead since that is more clearly named. Use '$toJSONString()' to get a JSON string")
      return(self$toSimpleType())
    },

    #' @description
    #' Convert to a List
    #'
    #' Convert the R6 object to a list to work more easily with other tooling.
    #'
    #' @return CompletionUsageCompletionTokensDetails as a base R list.
    #' @examples
    #' # convert array of CompletionUsageCompletionTokensDetails (x) to a data frame
    #' \dontrun{
    #' library(purrr)
    #' library(tibble)
    #' df <- x |> map(\(y)y$toList()) |> map(as_tibble) |> list_rbind()
    #' df
    #' }
    toList = function() {
      return(self$toSimpleType())
    },

    #' @description
    #' Convert CompletionUsageCompletionTokensDetails to a base R type
    #'
    #' @return A base R type, e.g. a list or numeric/character array.
    toSimpleType = function() {
      CompletionUsageCompletionTokensDetailsObject <- list()
      if (!is.null(self$`accepted_prediction_tokens`)) {
        CompletionUsageCompletionTokensDetailsObject[["accepted_prediction_tokens"]] <-
          self$`accepted_prediction_tokens`
      }
      if (!is.null(self$`audio_tokens`)) {
        CompletionUsageCompletionTokensDetailsObject[["audio_tokens"]] <-
          self$`audio_tokens`
      }
      if (!is.null(self$`reasoning_tokens`)) {
        CompletionUsageCompletionTokensDetailsObject[["reasoning_tokens"]] <-
          self$`reasoning_tokens`
      }
      if (!is.null(self$`rejected_prediction_tokens`)) {
        CompletionUsageCompletionTokensDetailsObject[["rejected_prediction_tokens"]] <-
          self$`rejected_prediction_tokens`
      }
      return(CompletionUsageCompletionTokensDetailsObject)
    },

    #' @description
    #' Deserialize JSON string into an instance of CompletionUsageCompletionTokensDetails
    #'
    #' @param input_json the JSON input
    #' @return the instance of CompletionUsageCompletionTokensDetails
    fromJSON = function(input_json) {
      this_object <- jsonlite::fromJSON(input_json)
      if (!is.null(this_object$`accepted_prediction_tokens`)) {
        self$`accepted_prediction_tokens` <- this_object$`accepted_prediction_tokens`
      }
      if (!is.null(this_object$`audio_tokens`)) {
        self$`audio_tokens` <- this_object$`audio_tokens`
      }
      if (!is.null(this_object$`reasoning_tokens`)) {
        self$`reasoning_tokens` <- this_object$`reasoning_tokens`
      }
      if (!is.null(this_object$`rejected_prediction_tokens`)) {
        self$`rejected_prediction_tokens` <- this_object$`rejected_prediction_tokens`
      }
      self
    },

    #' @description
    #' To JSON String
    #' 
    #' @param ... Parameters passed to `jsonlite::toJSON`
    #' @return CompletionUsageCompletionTokensDetails in JSON format
    toJSONString = function(...) {
      simple <- self$toSimpleType()
      json <- jsonlite::toJSON(simple, auto_unbox = TRUE, digits = NA, ...)
      return(as.character(jsonlite::minify(json)))
    },

    #' @description
    #' Deserialize JSON string into an instance of CompletionUsageCompletionTokensDetails
    #'
    #' @param input_json the JSON input
    #' @return the instance of CompletionUsageCompletionTokensDetails
    fromJSONString = function(input_json) {
      this_object <- jsonlite::fromJSON(input_json)
      self$`accepted_prediction_tokens` <- this_object$`accepted_prediction_tokens`
      self$`audio_tokens` <- this_object$`audio_tokens`
      self$`reasoning_tokens` <- this_object$`reasoning_tokens`
      self$`rejected_prediction_tokens` <- this_object$`rejected_prediction_tokens`
      self
    },

    #' @description
    #' Validate JSON input with respect to CompletionUsageCompletionTokensDetails and throw an exception if invalid
    #'
    #' @param input the JSON input
    validateJSON = function(input) {
      input_json <- jsonlite::fromJSON(input)
    },

    #' @description
    #' To string (JSON format)
    #'
    #' @return String representation of CompletionUsageCompletionTokensDetails
    toString = function() {
      self$toJSONString()
    },

    #' @description
    #' Return true if the values in all fields are valid.
    #'
    #' @return true if the values in all fields are valid.
    isValid = function() {
      TRUE
    },

    #' @description
    #' Return a list of invalid fields (if any).
    #'
    #' @return A list of invalid fields (if any).
    getInvalidFields = function() {
      invalid_fields <- list()
      invalid_fields
    },

    #' @description
    #' Print the object
    print = function() {
      print(jsonlite::prettify(self$toJSONString()))
      invisible(self)
    }
  ),
  # Lock the class to prevent modifications to the method or field
  lock_class = TRUE
)
