#' Create a new MessageContentRefusalObject
#'
#' @description
#' The refusal content generated by the assistant.
#'
#' @docType class
#' @title MessageContentRefusalObject
#' @description MessageContentRefusalObject Class
#' @format An \code{R6Class} generator object
#' @field type Always `refusal`. character
#' @field refusal  character
#' @importFrom R6 R6Class
#' @importFrom jsonlite fromJSON toJSON
#' @export
MessageContentRefusalObject <- R6::R6Class(
  "MessageContentRefusalObject",
  public = list(
    `type` = NULL,
    `refusal` = NULL,

    #' @description
    #' Initialize a new MessageContentRefusalObject class.
    #'
    #' @param type Always `refusal`.
    #' @param refusal refusal
    #' @param ... Other optional arguments.
    initialize = function(`type`, `refusal`, ...) {
      if (!missing(`type`)) {
        if (!(`type` %in% c("refusal"))) {
          stop(paste("Error! \"", `type`, "\" cannot be assigned to `type`. Must be \"refusal\".", sep = ""))
        }
        if (!(is.character(`type`) && length(`type`) == 1)) {
          stop(paste("Error! Invalid data for `type`. Must be a string:", `type`))
        }
        self$`type` <- `type`
      }
      if (!missing(`refusal`)) {
        if (!(is.character(`refusal`) && length(`refusal`) == 1)) {
          stop(paste("Error! Invalid data for `refusal`. Must be a string:", `refusal`))
        }
        self$`refusal` <- `refusal`
      }
    },

    #' @description
    #' Convert to an R object. This method is deprecated. Use `toSimpleType()` instead.
    toJSON = function() {
      .Deprecated(new = "toSimpleType", msg = "Use the '$toSimpleType()' method instead since that is more clearly named. Use '$toJSONString()' to get a JSON string")
      return(self$toSimpleType())
    },

    #' @description
    #' Convert to a List
    #'
    #' Convert the R6 object to a list to work more easily with other tooling.
    #'
    #' @return MessageContentRefusalObject as a base R list.
    #' @examples
    #' # convert array of MessageContentRefusalObject (x) to a data frame
    #' \dontrun{
    #' library(purrr)
    #' library(tibble)
    #' df <- x |> map(\(y)y$toList()) |> map(as_tibble) |> list_rbind()
    #' df
    #' }
    toList = function() {
      return(self$toSimpleType())
    },

    #' @description
    #' Convert MessageContentRefusalObject to a base R type
    #'
    #' @return A base R type, e.g. a list or numeric/character array.
    toSimpleType = function() {
      MessageContentRefusalObjectObject <- list()
      if (!is.null(self$`type`)) {
        MessageContentRefusalObjectObject[["type"]] <-
          self$`type`
      }
      if (!is.null(self$`refusal`)) {
        MessageContentRefusalObjectObject[["refusal"]] <-
          self$`refusal`
      }
      return(MessageContentRefusalObjectObject)
    },

    #' @description
    #' Deserialize JSON string into an instance of MessageContentRefusalObject
    #'
    #' @param input_json the JSON input
    #' @return the instance of MessageContentRefusalObject
    fromJSON = function(input_json) {
      this_object <- jsonlite::fromJSON(input_json)
      if (!is.null(this_object$`type`)) {
        if (!is.null(this_object$`type`) && !(this_object$`type` %in% c("refusal"))) {
          stop(paste("Error! \"", this_object$`type`, "\" cannot be assigned to `type`. Must be \"refusal\".", sep = ""))
        }
        self$`type` <- this_object$`type`
      }
      if (!is.null(this_object$`refusal`)) {
        self$`refusal` <- this_object$`refusal`
      }
      self
    },

    #' @description
    #' To JSON String
    #' 
    #' @param ... Parameters passed to `jsonlite::toJSON`
    #' @return MessageContentRefusalObject in JSON format
    toJSONString = function(...) {
      simple <- self$toSimpleType()
      json <- jsonlite::toJSON(simple, auto_unbox = TRUE, digits = NA, ...)
      return(as.character(jsonlite::minify(json)))
    },

    #' @description
    #' Deserialize JSON string into an instance of MessageContentRefusalObject
    #'
    #' @param input_json the JSON input
    #' @return the instance of MessageContentRefusalObject
    fromJSONString = function(input_json) {
      this_object <- jsonlite::fromJSON(input_json)
      if (!is.null(this_object$`type`) && !(this_object$`type` %in% c("refusal"))) {
        stop(paste("Error! \"", this_object$`type`, "\" cannot be assigned to `type`. Must be \"refusal\".", sep = ""))
      }
      self$`type` <- this_object$`type`
      self$`refusal` <- this_object$`refusal`
      self
    },

    #' @description
    #' Validate JSON input with respect to MessageContentRefusalObject and throw an exception if invalid
    #'
    #' @param input the JSON input
    validateJSON = function(input) {
      input_json <- jsonlite::fromJSON(input)
      # check the required field `type`
      if (!is.null(input_json$`type`)) {
        if (!(is.character(input_json$`type`) && length(input_json$`type`) == 1)) {
          stop(paste("Error! Invalid data for `type`. Must be a string:", input_json$`type`))
        }
      } else {
        stop(paste("The JSON input `", input, "` is invalid for MessageContentRefusalObject: the required field `type` is missing."))
      }
      # check the required field `refusal`
      if (!is.null(input_json$`refusal`)) {
        if (!(is.character(input_json$`refusal`) && length(input_json$`refusal`) == 1)) {
          stop(paste("Error! Invalid data for `refusal`. Must be a string:", input_json$`refusal`))
        }
      } else {
        stop(paste("The JSON input `", input, "` is invalid for MessageContentRefusalObject: the required field `refusal` is missing."))
      }
    },

    #' @description
    #' To string (JSON format)
    #'
    #' @return String representation of MessageContentRefusalObject
    toString = function() {
      self$toJSONString()
    },

    #' @description
    #' Return true if the values in all fields are valid.
    #'
    #' @return true if the values in all fields are valid.
    isValid = function() {
      # check if the required `type` is null
      if (is.null(self$`type`)) {
        return(FALSE)
      }

      # check if the required `refusal` is null
      if (is.null(self$`refusal`)) {
        return(FALSE)
      }

      TRUE
    },

    #' @description
    #' Return a list of invalid fields (if any).
    #'
    #' @return A list of invalid fields (if any).
    getInvalidFields = function() {
      invalid_fields <- list()
      # check if the required `type` is null
      if (is.null(self$`type`)) {
        invalid_fields["type"] <- "Non-nullable required field `type` cannot be null."
      }

      # check if the required `refusal` is null
      if (is.null(self$`refusal`)) {
        invalid_fields["refusal"] <- "Non-nullable required field `refusal` cannot be null."
      }

      invalid_fields
    },

    #' @description
    #' Print the object
    print = function() {
      print(jsonlite::prettify(self$toJSONString()))
      invisible(self)
    }
  ),
  # Lock the class to prevent modifications to the method or field
  lock_class = TRUE
)
