package org.openapitools.server.model


/**
 * = Assistant message =
 *
 * @param content The contents of the assistant message. Required unless `tool_calls` or `function_call` is specified.  for example: ''null''
 * @param role The role of the messages author, in this case `assistant`. for example: ''null''
 * @param name An optional name for the participant. Provides the model information to differentiate between participants of the same role. for example: ''null''
 * @param toolCalls The tool calls generated by the model, such as function calls. for example: ''null''
 * @param functionCall  for example: ''null''
*/
final case class ChatCompletionRequestAssistantMessage (
  content: Option[String] = None,
  role: String,
  name: Option[String] = None,
  toolCalls: Option[Seq[ChatCompletionMessageToolCall]] = None,
  functionCall: Option[ChatCompletionRequestAssistantMessageFunctionCall] = None
)

