package org.openapitools.server.model


/**
 * @param content The contents of the function message. for example: ''null''
 * @param role The role of the messages author, in this case `function`. for example: ''null''
 * @param name The name of the function to call. for example: ''null''
 * @param refusal The refusal message by the assistant. for example: ''null''
 * @param audio  for example: ''null''
 * @param toolCalls The tool calls generated by the model, such as function calls. for example: ''null''
 * @param functionCall  for example: ''null''
 * @param toolCallId Tool call that this message is responding to. for example: ''null''
*/
final case class ChatCompletionRequestMessage (
  content: String,
  role: String,
  name: String,
  refusal: Option[String] = None,
  audio: Option[ChatCompletionRequestAssistantMessageAudio] = None,
  toolCalls: Option[Seq[ChatCompletionMessageToolCall]] = None,
  functionCall: Option[ChatCompletionRequestAssistantMessageFunctionCall] = None,
  toolCallId: String
)

