package org.openapitools.server.model


/**
 * A chat completion message generated by the model.
 *
 * @param content The contents of the message. for example: ''null''
 * @param refusal The refusal message generated by the model. for example: ''null''
 * @param toolCalls The tool calls generated by the model, such as function calls. for example: ''null''
 * @param role The role of the author of this message. for example: ''null''
 * @param functionCall  for example: ''null''
 * @param audio  for example: ''null''
*/
final case class ChatCompletionResponseMessage (
  content: String,
  refusal: String,
  toolCalls: Option[Seq[ChatCompletionMessageToolCall]] = None,
  role: String,
  functionCall: Option[ChatCompletionResponseMessageFunctionCall] = None,
  audio: Option[ChatCompletionResponseMessageAudio] = None
)

