package org.openapitools.server.model


/**
 * A chat completion delta generated by streamed model responses.
 *
 * @param content The contents of the chunk message. for example: ''null''
 * @param functionCall  for example: ''null''
 * @param toolCalls  for example: ''null''
 * @param role The role of the author of this message. for example: ''null''
 * @param refusal The refusal message generated by the model. for example: ''null''
*/
final case class ChatCompletionStreamResponseDelta (
  content: Option[String] = None,
  functionCall: Option[ChatCompletionStreamResponseDeltaFunctionCall] = None,
  toolCalls: Option[Seq[ChatCompletionMessageToolCallChunk]] = None,
  role: Option[String] = None,
  refusal: Option[String] = None
)

