
package org.openapitools.client.model


case class ChatCompletionRequestAssistantMessage (
    /* The contents of the assistant message. Required unless `tool_calls` or `function_call` is specified.  */
    _content: Option[String],
    /* The role of the messages author, in this case `assistant`. */
    _role: String,
    /* An optional name for the participant. Provides the model information to differentiate between participants of the same role. */
    _name: Option[String],
    /* The tool calls generated by the model, such as function calls. */
    _toolCalls: Option[List[ChatCompletionMessageToolCall]],
    _functionCall: Option[ChatCompletionRequestAssistantMessageFunctionCall]
)
object ChatCompletionRequestAssistantMessage {
    def toStringBody(var_content: Object, var_role: Object, var_name: Object, var_toolCalls: Object, var_functionCall: Object) =
        s"""
        | {
        | "content":$var_content,"role":$var_role,"name":$var_name,"toolCalls":$var_toolCalls,"functionCall":$var_functionCall
        | }
        """.stripMargin
}
