
package org.openapitools.client.model


case class ChatCompletionRequestMessage (
    /* The contents of the function message. */
    _content: String,
    /* The role of the messages author, in this case `function`. */
    _role: String,
    /* The name of the function to call. */
    _name: String,
    /* The refusal message by the assistant. */
    _refusal: Option[String],
    _audio: Option[ChatCompletionRequestAssistantMessageAudio],
    /* The tool calls generated by the model, such as function calls. */
    _toolCalls: Option[List[ChatCompletionMessageToolCall]],
    _functionCall: Option[ChatCompletionRequestAssistantMessageFunctionCall],
    /* Tool call that this message is responding to. */
    _toolCallId: String
)
object ChatCompletionRequestMessage {
    def toStringBody(var_content: Object, var_role: Object, var_name: Object, var_refusal: Object, var_audio: Object, var_toolCalls: Object, var_functionCall: Object, var_toolCallId: Object) =
        s"""
        | {
        | "content":$var_content,"role":$var_role,"name":$var_name,"refusal":$var_refusal,"audio":$var_audio,"toolCalls":$var_toolCalls,"functionCall":$var_functionCall,"toolCallId":$var_toolCallId
        | }
        """.stripMargin
}
