object CreateChatCompletionRequest {
    def toStringBody(var_messages: Object, var_model: Object, var_store: Object, var_reasoningEffort: Object, var_metadata: Object, var_frequencyPenalty: Object, var_logitBias: Object, var_logprobs: Object, var_topLogprobs: Object, var_maxTokens: Object, var_maxCompletionTokens: Object, var_n: Object, var_modalities: Object, var_prediction: Object, var_audio: Object, var_presencePenalty: Object, var_responseFormat: Object, var_seed: Object, var_serviceTier: Object, var_stop: Object, var_stream: Object, var_streamOptions: Object, var_temperature: Object, var_topP: Object, var_tools: Object, var_toolChoice: Object, var_parallelToolCalls: Object, var_user: Object, var_functionCall: Object, var_functions: Object) =
