
package org.openapitools.client.model


case class FineTuneChatCompletionRequestAssistantMessage (
    _content: Option[ChatCompletionRequestAssistantMessageContent],
    /* The refusal message by the assistant. */
    _refusal: Option[String],
    /* The role of the messages author, in this case `assistant`. */
    _role: String,
    /* An optional name for the participant. Provides the model information to differentiate between participants of the same role. */
    _name: Option[String],
    _audio: Option[ChatCompletionRequestAssistantMessageAudio],
    /* The tool calls generated by the model, such as function calls. */
    _toolCalls: Option[List[ChatCompletionMessageToolCall]],
    _functionCall: Option[ChatCompletionRequestAssistantMessageFunctionCall],
    /* Controls whether the assistant message is trained against (0 or 1) */
    _weight: Option[Integer]
)
object FineTuneChatCompletionRequestAssistantMessage {
    def toStringBody(var_content: Object, var_refusal: Object, var_role: Object, var_name: Object, var_audio: Object, var_toolCalls: Object, var_functionCall: Object, var_weight: Object) =
        s"""
        | {
        | "content":$var_content,"refusal":$var_refusal,"role":$var_role,"name":$var_name,"audio":$var_audio,"toolCalls":$var_toolCalls,"functionCall":$var_functionCall,"weight":$var_weight
        | }
        """.stripMargin
}
