package org.openapitools.client.api

import argonaut._
import argonaut.EncodeJson._
import argonaut.DecodeJson._

import org.http4s.{EntityDecoder, EntityEncoder}
import org.http4s.argonaut._
import org.joda.time.DateTime


import CompletionUsageCompletionTokensDetails._

case class CompletionUsageCompletionTokensDetails (
  /* When using Predicted Outputs, the number of tokens in the prediction that appeared in the completion.  */
  acceptedPredictionTokens: Option[Integer],
/* Audio input tokens generated by the model. */
  audioTokens: Option[Integer],
/* Tokens generated by the model for reasoning. */
  reasoningTokens: Option[Integer],
/* When using Predicted Outputs, the number of tokens in the prediction that did not appear in the completion. However, like reasoning tokens, these tokens are still counted in the total completion tokens for purposes of billing, output, and context window limits.  */
  rejectedPredictionTokens: Option[Integer])

object CompletionUsageCompletionTokensDetails {
  import DateTimeCodecs._

  implicit val CompletionUsageCompletionTokensDetailsCodecJson: CodecJson[CompletionUsageCompletionTokensDetails] = CodecJson.derive[CompletionUsageCompletionTokensDetails]
  implicit val CompletionUsageCompletionTokensDetailsDecoder: EntityDecoder[CompletionUsageCompletionTokensDetails] = jsonOf[CompletionUsageCompletionTokensDetails]
  implicit val CompletionUsageCompletionTokensDetailsEncoder: EntityEncoder[CompletionUsageCompletionTokensDetails] = jsonEncoderOf[CompletionUsageCompletionTokensDetails]
}
